import React from "react";
import ReactDOM from "react-dom";

import Card from "./Card";
import Button from "./Button";
import clasess from "./ErrorModal.module.css";

const Backdrop = props => {

    <div className = {clasess.backdrop} onClick = {props.onConfirm}/>

}

const ModelOverly = props => {

    <Card className={clasess.modal}>

    <header className={clasess.header} >    
    <h2>{props.title}</h2>
    </header>
    <div className={clasess.content}>
        <p>{props.massage}</p>
    </div>

<footer className={clasess.actions}>
    <Button  onClick = {props.onConfirm} >Okay</Button>
</footer>


 </Card>

}

const ErrorModal = props => {

    return  (
        <React.Fragment>
       

            
            {ReactDOM.createPortal(<Backdrop onClick={props.onConfirm}/>, document.getElementById("backdrop-root"))}
            

         </React.Fragment>

    );

};

export default ErrorModal;
